mapper.select:
SELECT playDate, taiNo, rate, bonusCount, ballOutput, ballInput, totalOut, rateN, bonusCountN, ballOutputN, playDateN, totalOutN FROM
v_satama_by_d_t_09
v_satama_by_d_t_10
v_satama_by_d_t
v_satama_by_d_t
WHERE taiNo = #{taiNo}
AND playDate = #{playDate}
ORDER BY playDate ASC;

SELECT v.playDate, h.groupName, v.taiNo, v.rate, v.bonusCount, v.ballOutput, v.ballInput, v.totalOut, v.totalOutBefore, v.rateN, v.bonusCountN, v.ballOutputN, v.playDateN, v.totalOutN, b.rate as rate1, b.bonusCount as bonusCount1, b.ballOutput as ballOutput1, c.rate as rate2, c.bonusCount as bonusCount2, c.ballOutput as ballOutput2, d.rate as rate3, d.bonusCount as bonusCount3, d.ballOutput as ballOutput3, e.rate as rate4, e.bonusCount as bonusCount4, e.ballOutput as ballOutput4, f.rate as rate5, f.bonusCount as bonusCount5, f.ballOutput as ballOutput5, g.rate as rate6, g.bonusCount as bonusCount6, g.ballOutput as ballOutput6 FROM
v_satama_by_d_t_09 v
v_satama_by_d_t_10 v
v_satama_by_d_t v
v_satama_by_d_t v
LEFT OUTER JOIN piainfo b on (b.taino = v.taino and b.playDate = adddays(v.playDate,-1)) LEFT OUTER JOIN piainfo c on (c.taino = v.taino and c.playDate = adddays(v.playDate,-2)) LEFT OUTER JOIN piainfo d on (d.taino = v.taino and d.playDate = adddays(v.playDate,-3)) LEFT OUTER JOIN piainfo e on (e.taino = v.taino and e.playDate = adddays(v.playDate,-4)) LEFT OUTER JOIN piainfo f on (f.taino = v.taino and f.playDate = adddays(v.playDate,-5)) LEFT OUTER JOIN piainfo g on (g.taino = v.taino and g.playDate = adddays(v.playDate,-6)) LEFT OUTER JOIN taino h on (h.taino = v.taino) WHERE v.playDate = #{playDate}
ORDER BY ballOutput DESC;
ORDER BY totalOutBefore DESC;
ORDER BY totalOut DESC;
ORDER BY rate DESC;
ORDER BY rate1 DESC;

mapper.update:
UPDATE
	piainfo
SET
	rate = #{rate},
	bonusCount = #{bonusCount},
	ballOutput = #{ballOutput}
WHERE
	playDate = #{playDate}
AND
	taiNo = #{taiNo}

mapper.delete:
DELETE FROM piainfo 
WHERE
	playDate = #{playDate}
AND
	taiNo = #{taiNo}
